import React, { useState } from 'react';
import QRCode from 'qrcode.react';
import { v4 as uuidv4 } from 'uuid';
import { FileTransferService } from './services/fileTransferService';

// 文件大小限制（100MB）
const MAX_FILE_SIZE = 100 * 1024 * 1024; 

// 允许的文件类型
const ALLOWED_FILE_TYPES = [
  'image/jpeg', 
  'image/png', 
  'image/gif', 
  'application/pdf', 
  'text/plain', 
  'application/msword', 
  'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
];

const App: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [qrChunks, setQRChunks] = useState<string[]>([]);
  const [currentChunkIndex, setCurrentChunkIndex] = useState(0);
  const [receivedChunks, setReceivedChunks] = useState<string[]>([]);
  const [error, setError] = useState<string | null>(null);

  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];

    // 重置错误状态
    setError(null);

    if (file) {
      // 验证文件大小
      if (file.size > MAX_FILE_SIZE) {
        setError(`文件大小不能超过 ${MAX_FILE_SIZE / 1024 / 1024}MB`);
        return;
      }

      // 验证文件类型
      if (!ALLOWED_FILE_TYPES.includes(file.type)) {
        setError('不支持的文件类型');
        return;
      }

      try {
        const chunks = await FileTransferService.fileToQRChunks(file);
        setSelectedFile(file);
        setQRChunks(chunks);
        setCurrentChunkIndex(0);
      } catch (error) {
        console.error('文件处理错误:', error);
      }
    }
  };

  const handleQRScan = (data: string) => {
    try {
      const chunk = JSON.parse(data);
      setReceivedChunks(prev => [...prev, JSON.stringify(chunk)]);
    } catch (error) {
      console.error('二维码解析错误:', error);
    }
  };

  const reconstructFile = async () => {
    try {
      if (receivedChunks.length > 0) {
        const reconstructedFile = await FileTransferService.qrChunksToFile(
          receivedChunks, 
          selectedFile?.name || 'downloaded_file'
        );

        // 创建下载链接
        const url = URL.createObjectURL(reconstructedFile);
        const link = document.createElement('a');
        link.href = url;
        link.download = reconstructedFile.name;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      }
    } catch (error) {
      console.error('文件重组错误:', error);
    }
  };

  const renderQRCode = () => {
    if (!qrChunks.length) return null;

    return (
      <div>
        <QRCode 
          value={qrChunks[currentChunkIndex]} 
          size={256} 
          level={'H'} 
        />
        <p>
          分块 {currentChunkIndex + 1} / {qrChunks.length}
          <button 
            onClick={() => setCurrentChunkIndex(
              (prev) => (prev + 1) % qrChunks.length
            )}
          >
            下一个分块
          </button>
        </p>
      </div>
    );
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes < 1024) return `${bytes} B`;
    if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(2)} KB`;
    return `${(bytes / 1024 / 1024).toFixed(2)} MB`;
  };

  return (
    <div style={{ 
      maxWidth: '600px', 
      margin: '0 auto', 
      padding: '20px', 
      textAlign: 'center' 
    }}>
      <h1>无网络文件传输</h1>

      <input 
        type="file" 
        onChange={handleFileSelect}
        style={{ margin: '20px 0' }}
      />

      {error && (
        <div style={{ 
          color: 'red', 
          backgroundColor: '#ffeeee', 
          padding: '10px', 
          borderRadius: '5px', 
          marginBottom: '15px' 
        }}>
          {error}
        </div>
      )}

      {selectedFile && (
        <div style={{ 
          textAlign: 'center', 
          backgroundColor: '#f9f9f9', 
          padding: '20px', 
          borderRadius: '10px' 
        }}>
          <p>
            <strong>文件名:</strong> {selectedFile.name}
            <br />
            <strong>文件大小:</strong> {formatFileSize(selectedFile.size)}
            <br />
            <strong>文件类型:</strong> {selectedFile.type || '未知'}
          </p>

          {renderQRCode()}

          <div style={{ marginTop: '20px' }}>
            <button 
              onClick={reconstructFile}
              disabled={receivedChunks.length === 0}
            >
              重组文件
            </button>
          </div>
        </div>
      )}

      <div>
        <h2>接收文件</h2>
        <textarea 
          placeholder="扫描二维码后，粘贴二维码内容到这里"
          rows={5}
          style={{ width: '100%', marginBottom: '10px' }}
          onChange={(e) => {
            try {
              const chunk = JSON.parse(e.target.value);
              setReceivedChunks(prev => [...prev, JSON.stringify(chunk)]);
            } catch (error) {
              console.error('解析错误:', error);
            }
          }}
        />
        <p>已接收分块: {receivedChunks.length}</p>
      </div>
    </div>
  );
};

export default App;
