import React, { useState, useRef, useEffect } from 'react';
import QRCode from 'qrcode.react';
import { FileTransferService } from './services/fileTransferService';

const App: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [qrChunks, setQRChunks] = useState<string[]>([]);
  const [currentChunkIndex, setCurrentChunkIndex] = useState(0);
  const [receivedChunks, setReceivedChunks] = useState<string[]>([]);
  const [isTransmitting, setIsTransmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [progress, setProgress] = useState(0);
  const [theme, setTheme] = useState<'light' | 'dark'>('light');

  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const receivedChunksTextareaRef = useRef<HTMLTextAreaElement>(null);

  // ÂàáÊç¢‰∏ªÈ¢ò
  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
  };

  // Âä®ÊÄÅ‰∫åÁª¥Á†Å‰º†Ëæì
  const startDynamicQRTransmission = () => {
    if (qrChunks.length === 0) {
      setError('ËØ∑ÂÖàÈÄâÊã©Êñá‰ª∂');
      return;
    }

    setIsTransmitting(true);
    setCurrentChunkIndex(0);
    setProgress(0);

    intervalRef.current = setInterval(() => {
      setCurrentChunkIndex(prevIndex => {
        const nextIndex = (prevIndex + 1) % qrChunks.length;

        setProgress(Math.round((nextIndex / qrChunks.length) * 100));

        if (nextIndex === 0) {
          stopDynamicQRTransmission();
        }

        return nextIndex;
      });
    }, 500);
  };

  const stopDynamicQRTransmission = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
      setIsTransmitting(false);
    }
  };

  // Êñá‰ª∂ÈÄâÊã©Â§ÑÁêÜ
  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];

    if (file) {
      try {
        if (file.size > 5 * 1024 * 1024) {
          setError('Êñá‰ª∂ËøáÂ§ßÔºåËØ∑ÈÄâÊã©Â∞è‰∫é 5MB ÁöÑÊñá‰ª∂');
          return;
        }

        const chunks = await FileTransferService.fileToQRChunks(file);
        setSelectedFile(file);
        setQRChunks(chunks);
        setCurrentChunkIndex(0);
        setError(null);

        startDynamicQRTransmission();
      } catch (error) {
        console.error('Êñá‰ª∂Â§ÑÁêÜÈîôËØØ:', error);
        setError(error instanceof Error ? error.message : 'Êñá‰ª∂Â§ÑÁêÜÂ§±Ë¥•');
      }
    }
  };

  // Êñá‰ª∂ÈáçÁªÑ
  const reconstructFile = async () => {
    try {
      if (receivedChunks.length === 0) {
        setError('ËØ∑ÂÖàÊé•Êî∂Êñá‰ª∂ÂàÜÂùó');
        return;
      }

      const reconstructedFile = await FileTransferService.qrChunksToFile(
        receivedChunks, 
        selectedFile?.name || 'downloaded_file'
      );

      const url = URL.createObjectURL(reconstructedFile);
      const link = document.createElement('a');
      link.href = url;
      link.download = reconstructedFile.name;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      setReceivedChunks([]);
      setError(null);
    } catch (error) {
      console.error('Êñá‰ª∂ÈáçÁªÑÈîôËØØ:', error);
      setError(error instanceof Error ? error.message : 'Êñá‰ª∂ÈáçÁªÑÂ§±Ë¥•');
    }
  };

  // Ê∏≤Êüì‰∫åÁª¥Á†ÅÂå∫Âüü
  const renderQRCode = () => {
    if (!qrChunks.length) return null;

    return (
      <div className={`
        flex flex-col items-center space-y-4 p-4 rounded-lg 
        ${theme === 'light' 
          ? 'bg-white/80 shadow-md' 
          : 'bg-gray-800/80 shadow-xl'
        } transition-all duration-300
      `}>
        <div className="relative">
          <QRCode 
            value={qrChunks[currentChunkIndex]} 
            size={256} 
            level={'H'} 
            className={`
              shadow-lg rounded-lg 
              ${theme === 'light' 
                ? 'border-4 border-blue-100' 
                : 'border-4 border-gray-700'
              }
            `}
          />
          <div className="absolute top-2 right-2">
            <span className={`
              px-2 py-1 rounded-full text-xs font-bold
              ${theme === 'light' 
                ? 'bg-blue-100 text-blue-800' 
                : 'bg-gray-700 text-white'
              }
            `}>
              ÂàÜÂùó {currentChunkIndex + 1} / {qrChunks.length}
            </span>
          </div>
        </div>

        <div className="flex items-center space-x-4">
          {isTransmitting ? (
            <button 
              onClick={stopDynamicQRTransmission}
              className={`
                px-4 py-2 rounded-md transition-colors
                ${theme === 'light'
                  ? 'bg-red-500 hover:bg-red-600 text-white'
                  : 'bg-red-700 hover:bg-red-800 text-gray-100'
                }
              `}
            >
              ÂÅúÊ≠¢‰º†Ëæì
            </button>
          ) : (
            <button 
              onClick={startDynamicQRTransmission}
              className={`
                px-4 py-2 rounded-md transition-colors
                ${theme === 'light'
                  ? 'bg-blue-500 hover:bg-blue-600 text-white'
                  : 'bg-blue-700 hover:bg-blue-800 text-gray-100'
                }
              `}
            >
              ÂºÄÂßãÂä®ÊÄÅ‰º†Ëæì
            </button>
          )}
        </div>

        <div className={`
          w-full h-2.5 rounded-full 
          ${theme === 'light' ? 'bg-gray-200' : 'bg-gray-700'}
        `}>
          <div 
            className={`
              h-2.5 rounded-full transition-all duration-500 ease-in-out
              ${theme === 'light' 
                ? 'bg-blue-600' 
                : 'bg-blue-500'
              }
            `}
            style={{ width: `${progress}%` }}
          />
        </div>
      </div>
    );
  };

  // ‰∏ªÈ¢òÁâπÂÆöÁöÑËÉåÊôØÂíåÊñáÂ≠óÈ¢úËâ≤
  const backgroundClass = theme === 'light' 
    ? 'bg-gradient-to-br from-blue-50 to-blue-100' 
    : 'bg-gradient-to-br from-gray-900 to-gray-800';

  const textColorClass = theme === 'light' 
    ? 'text-gray-800' 
    : 'text-gray-100';

  const cardClass = theme === 'light'
    ? 'bg-white shadow-2xl'
    : 'bg-gray-800 shadow-2xl border border-gray-700';

  return (
    <div className={`
      min-h-screen ${backgroundClass} 
      flex items-center justify-center p-4 
      transition-all duration-300
    `}>
      <div className="absolute top-4 right-4">
        <button 
          onClick={toggleTheme}
          className={`
            p-2 rounded-full transition-colors
            ${theme === 'light'
              ? 'bg-gray-200 text-gray-800 hover:bg-gray-300'
              : 'bg-gray-700 text-white hover:bg-gray-600'
            }
          `}
        >
          {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
        </button>
      </div>

      <div className={`
        ${cardClass} rounded-2xl p-8 w-full max-w-md space-y-6
        ${textColorClass} transition-all duration-300
      `}>
        <h1 className={`
          text-3xl font-bold text-center 
          ${theme === 'light' ? 'text-blue-600' : 'text-blue-400'}
        `}>
          Âä®ÊÄÅ‰∫åÁª¥Á†ÅÊñá‰ª∂‰º†Ëæì
        </h1>

        {error && (
          <div className={`
            border px-4 py-3 rounded relative
            ${theme === 'light'
