import React, { useState, useRef } from 'react';
import QRCode from 'qrcode.react';
import { FileTransferService } from './services/fileTransferService';

const App: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [qrChunks, setQRChunks] = useState<string[]>([]);
  const [currentChunkIndex, setCurrentChunkIndex] = useState(0);
  const [receivedChunks, setReceivedChunks] = useState<string[]>([]);
  const [isTransmitting, setIsTransmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [progress, setProgress] = useState(0);

  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const startDynamicQRTransmission = () => {
    if (qrChunks.length === 0) {
      setError('请先选择文件');
      return;
    }

    setIsTransmitting(true);
    setCurrentChunkIndex(0);
    setProgress(0);

    intervalRef.current = setInterval(() => {
      setCurrentChunkIndex(prevIndex => {
        const nextIndex = (prevIndex + 1) % qrChunks.length;

        setProgress(Math.round((nextIndex / qrChunks.length) * 100));

        if (nextIndex === 0) {
          stopDynamicQRTransmission();
        }

        return nextIndex;
      });
    }, 500);
  };

  const stopDynamicQRTransmission = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
      setIsTransmitting(false);
    }
  };

  const handleFileSelect = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];

    if (file) {
      try {
        if (file.size > 1 * 1024 * 1024) {
          setError('文件过大，请选择小于 1MB 的文件');
          return;
        }

        const chunks = await FileTransferService.fileToQRChunks(file);
        setSelectedFile(file);
        setQRChunks(chunks);
        setCurrentChunkIndex(0);
        setError(null);

        startDynamicQRTransmission();
      } catch (error) {
        console.error('文件处理错误:', error);
        setError(error instanceof Error ? error.message : '文件处理失败');
      }
    }
  };

  const reconstructFile = async () => {
    try {
      if (receivedChunks.length === 0) {
        setError('请先接收文件分块');
        return;
      }

      const reconstructedFile = await FileTransferService.qrChunksToFile(
        receivedChunks, 
        selectedFile?.name || 'downloaded_file'
      );

      const url = URL.createObjectURL(reconstructedFile);
      const link = document.createElement('a');
      link.href = url;
      link.download = reconstructedFile.name;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      setReceivedChunks([]);
      setError(null);
    } catch (error) {
      console.error('文件重组错误:', error);
      setError(error instanceof Error ? error.message : '文件重组失败');
    }
  };

  const renderQRCode = () => {
    if (!qrChunks.length) return null;

    return (
      <div className="flex flex-col items-center space-y-4">
        <QRCode 
          value={qrChunks[currentChunkIndex]} 
          size={256} 
          level={'H'} 
          className="shadow-lg rounded-lg"
        />
        <div className="flex items-center space-x-4">
          <span className="text-gray-600">
            分块 {currentChunkIndex + 1} / {qrChunks.length}
          </span>
          {isTransmitting ? (
            <button 
              onClick={stopDynamicQRTransmission}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md transition-colors"
            >
              停止传输
            </button>
          ) : (
            <button 
              onClick={startDynamicQRTransmission}
              className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md transition-colors"
            >
              开始动态传输
            </button>
          )}
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div 
            className="bg-blue-600 h-2.5 rounded-full transition-all duration-500 ease-in-out" 
            style={{ width: `${progress}%` }}
          />
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 flex items-center justify-center p-4">
      <div className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-md space-y-6">
        <h1 className="text-3xl font-bold text-center text-blue-600">
          动态二维码文件传输
        </h1>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
            {error}
          </div>
        )}

        <div className="flex flex-col items-center space-y-4">
          <input 
            type="file" 
            ref={fileInputRef}
            onChange={handleFileSelect}
            className="hidden"
          />
          <button 
            onClick={() => fileInputRef.current?.click()}
            className="bg-green-500 hover:bg-green-600 text-white px-6 py-3 rounded-lg transition-colors flex items-center space-x-2"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
            <span>选择文件</span>
          </button>

          {selectedFile && (
            <div className="text-gray-600">
              已选择: {selectedFile.name} ({(selectedFile.size / 1024).toFixed(2)} KB)
            </div>
          )}
        </div>

        {selectedFile && renderQRCode()}

        <div className="space-y-4">
          <h2 className="text-xl font-semibold text-gray-700">接收文件</h2>
          <textarea 
            placeholder="扫描二维码后，粘贴二维码内容到这里"
            rows={5}
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            onChange={(e) => {
              try {
                const chunk = JSON.parse(e.target.value);
                setReceivedChunks(prev => {
                  if (!prev.some(existingChunk => 
                    JSON.parse(existingChunk).index === chunk.index
                  )) {
                    return [...prev, JSON.stringify(chunk)];
                  }
                  return prev;
                });
                setError(null);
              } catch (error) {
                console.error('解析错误:', error);
                setError('二维码内容解析失败');
              }
            }}
          />
          <button 
            onClick={reconstructFile}
            className="w-full bg-indigo-500 hover:bg-indigo-600 text-white px-6 py-3 rounded-lg transition-colors"
          >
            重组文件
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
