import React, { useState } from 'react';
import QRCode from 'qrcode.react';
import { v4 as uuidv4 } from 'uuid';

// 文件大小限制（100MB）
const MAX_FILE_SIZE = 100 * 1024 * 1024; 

// 允许的文件类型
const ALLOWED_FILE_TYPES = [
  'image/jpeg', 
  'image/png', 
  'image/gif', 
  'application/pdf', 
  'text/plain', 
  'application/msword', 
  'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
];

const App: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [qrCodeData, setQRCodeData] = useState<string>('');
  const [error, setError] = useState<string | null>(null);

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];

    // 重置错误状态
    setError(null);

    if (file) {
      // 验证文件大小
      if (file.size > MAX_FILE_SIZE) {
        setError(`文件大小不能超过 ${MAX_FILE_SIZE / 1024 / 1024}MB`);
        return;
      }

      // 验证文件类型
      if (!ALLOWED_FILE_TYPES.includes(file.type)) {
        setError('不支持的文件类型');
        return;
      }

      setSelectedFile(file);

      // 生成唯一的传输会话ID
      const sessionId = `transfer-${uuidv4()}`;
      setQRCodeData(sessionId);

      console.log('Selected file:', file.name);
      console.log('Session ID:', sessionId);
    }
  };

  const handleDownloadQR = () => {
    const canvas = document.querySelector('canvas') as HTMLCanvasElement;
    const link = document.createElement('a');
    link.download = 'qr-code.png';
    link.href = canvas.toDataURL();
    link.click();
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes < 1024) return `${bytes} B`;
    if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(2)} KB`;
    return `${(bytes / 1024 / 1024).toFixed(2)} MB`;
  };

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center', 
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      <h1 style={{ color: '#333' }}>文件传输二维码生成器</h1>

      <input 
        type="file" 
        onChange={handleFileSelect} 
        style={{ 
          margin: '20px 0', 
          padding: '10px',
          border: '2px dashed #ccc',
          borderRadius: '8px'
        }}
      />

      {error && (
        <div style={{ 
          color: 'red', 
          backgroundColor: '#ffeeee', 
          padding: '10px', 
          borderRadius: '5px', 
          marginBottom: '15px' 
        }}>
          {error}
        </div>
      )}

      {selectedFile && (
        <div style={{ 
          textAlign: 'center', 
          backgroundColor: '#f9f9f9', 
          padding: '20px', 
          borderRadius: '10px' 
        }}>
          <p>
            <strong>文件名:</strong> {selectedFile.name}
            <br />
            <strong>文件大小:</strong> {formatFileSize(selectedFile.size)}
            <br />
            <strong>文件类型:</strong> {selectedFile.type || '未知'}
          </p>

          <div style={{ 
            border: '2px solid #ddd', 
            padding: '15px', 
            borderRadius: '8px',
            display: 'inline-block',
            marginTop: '15px'
          }}>
            <QRCode 
              value={qrCodeData} 
              size={256} 
              level={'H'} 
              fgColor="#333"
              bgColor="#fff"
            />
          </div>

          <div style={{ marginTop: '20px' }}>
            <button 
              onClick={handleDownloadQR}
              style={{
                padding: '10px 20px',
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                fontSize: '16px',
                transition: 'background-color 0.3s'
              }}
              onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#45a049'}
              onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#4CAF50'}
            >
              下载二维码
            </button>
          </div>
        </div>
      )}

      <div style={{ 
        marginTop: '30px', 
        color: '#666', 
        textAlign: 'center',
        maxWidth: '500px'
      }}>
        <p>
          支持的文件类型：图片(JPG/PNG/GIF)、PDF、文本文件、Word文档
          <br />
          最大文件大小：{MAX_FILE_SIZE / 1024 / 1024} MB
        </p>
      </div>
    </div>
  );
};

export default App;
